    public function executeFailCases(): array
    {
        return [
            ['{}', '{}'],
            ['', "{}"],
            [null, "{}"],
            ['{type: ""}', '{}'],
            ['{value: ""}', '{}']
        ];
    }

    /**
     * @dataProvider executeFailCases
     */
    public function testExecuteFails($gbStr, $expectedStr)
    {
        $globalBlock = json_decode($gbStr);

        $this->expectException(\Exception::class);
        $context = new WPGlobalBlockRulesContext($globalBlock);
        $this->internalExecute($gbStr, $expectedStr, $context);
    }

    /**
     * @dataProvider executeUseCases
     * @throws \Exception
     */
    public function testExecute($gbStr, $expectedStr)
    {
        $globalBlock = json_decode($gbStr);

        $context = new WPGlobalBlockRulesContext($globalBlock);
        $this->internalExecute($gbStr, $expectedStr, $context);
    }

    /**
     * @param $gbStr
     * @param $expectedStr
     * @param WPGlobalBlockRulesContext $context
     * @throws \Exception
     */
    protected function internalExecute($gbStr, $expectedStr, WPGlobalBlockRulesContext $context): void
    {
        $globalBlock = json_decode($gbStr);
        $expected = json_decode($expectedStr);

        $transformer = new WPGlobalBlockRulesTransformer($globalBlock);
        $transformer->execute($context);
        $data = $context->getData();

        $this->assertEquals($data, $expected, "Migrated fails on rules");
    }
